@use '../abstracts' as *;

.navigation {
	// height: 10vh;

	&__nav {
		position: absolute;
		top: 5rem;
		right: 5rem;
		// background-color: #ffffff;
		background-color: transparent;
		// background-color: $color-ice-gold;
	}

	&__list {
		display: flex;
		flex-direction: column;
		// justify-content: space-around;
		// align-items: baseline;
		// color: #fff;
		// padding: 1.5rem 0 0.5rem 0;
		font-size: 1.8rem;
		transition: transform 2s ease-out;
	}

	&__item {
		position: relative;
		color: $color-primary;
		background-color: transparent;
		opacity: 0;
		padding: 0 2rem;
		// margin: 0.5rem 0;
		border-radius: 3px;
		font-size: 3rem;
		font-weight: 500;
		text-align: right;
		transition: all 0.25s ease-out;

		&:not(:last-child) {
			margin-bottom: 1rem;
		}
	}

	&__item::before {
		content: '';
		position: absolute;
		top: 0;
		right: 0;
		height: 100%;
		width: 3px;
		background-color: $color-primary;
		transform: scaleY(0);
		transition: transform 0.3s, width 0.4s ease-out 0.2s;
	}

	&__item:hover::before {
		transform: scaleY(1);
		width: 100%;
	}

	&__link:link,
	&__link:visited {
		color: $color-primary;
		text-decoration: none;
		display: block;
		position: relative;
		z-index: 10;
		transition: color 0.1s 0.2s;
	}

	&__link:hover {
		color: $color-grey-dark;
	}
}

