		width: 50%;
	}

	&__input {
		font-size: 1.4rem;
		font-family: inherit;
		color: inherit;
		padding: 1.5rem 2rem;
		border-radius: 5px;
		// background-color: rgba(#fff, 0.5);
		// border: none;
		border-bottom: 3px solid transparent;
		width: 100%;
		display: block;
		transition: all 0.3s;

		/*
		@include respond(tab-port) {
			width: 100%;
		}
		*/

		&:focus {
			outline: none;
			box-shadow: 0 1rem 2rem rgba(black, 0.1);
			border-bottom: 3px solid $color-grey-dark-3;
		}

		&:focus:invalid {
			border-bottom: 3px solid $color-grey-dark-3;
		}

		&::-webkit-input-placeholder {
			color: $color-grey-dark-3;
		}
	}

	&__label {
		font-size: 1.4rem;
		color: $color-grey-dark-3;
		font-weight: 400;
		margin-left: 2rem;
		margin-top: 0.7rem;
		display: block;
		transition: all 0.3s;
	}

	&__input:placeholder-shown + &__label {
		opacity: 0;
		visibility: hidden;
		transform: translateY(-4rem);
	}
}

.push-button {
	position: relative;
	border: none;
	background: transparent;
	padding: 0;
	cursor: pointer;
	outline-offset: 4px;
	transition: filter 250ms;
	//FIXME
	-webkit-tap-highlight-color: transparent;
	user-select: none;
}

.shadow {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	border-radius: 10px;
	background: hsla(0, 0%, 0%, 0.25);
	will-change: transform; //NOTE
	transform: translateY(2px);
	transition: transform 600ms cubic-bezier(0.3, 0.7, 0.4, 1);
}

.edge {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	border-radius: 10px;
	background: linear-gradient(
		to left,
		hsl(220, 50%, 30%) 0%,
		hsl(220, 100%, 57%) 8%,
		hsl(220, 100%, 60%) 92%,
		hsl(220, 50%, 30%) 100%
	);
}

.front {
	display: block;
	position: relative;
	padding: 12px 60px;
	border-radius: 10px;
	font-size: 1.25rem;
	color: white;
	background: hsl(220, 1%, 45%);
	will-change: transform;
	transform: translateY(-4px);
	transition: transform 600ms cubic-bezier(0.3, 0.7, 0.4, 1);
}

.push-button:hover {
	filter: brightness(105%);
}

.push-button:hover .front {
	transform: translateY(-5px);
	transition: treansform 250ms cubic-bezier(0.3, 0.7, 0.4, 1.25);
}

.push-button:hover .shadow {
	transform: translateY(3px);
	transition: treansform 250ms cubic-bezier(0.3, 0.7, 0.4, 1.25);
}
.push-button:active .front {
	transform: translateY(-2px);
	transition: transform 34ms;
}
.push-button:active .shadow {
	transform: translateY(1px);
	transition: transform 34ms;
}

.push-button:focus:not(:focus-visible) {
	outline: none;
}

