@use '../abstracts' as *;

.header {
	&__primary {
		background-color: $color-ice-gold;
		color: black;
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;
		padding: 1rem 0;
	}

	&__name {
		opacity: 1;
		font-size: 9rem;
		font-family: $font-special;
		line-height: 1.25;
	}

	&__title {
		opacity: 0;
		font-size: 4rem;
		line-height: 1.25;
	}
}

a:visited {
	color: black;
}

