$color-grey-light-2: #eee;

$color-grey-dark: #777;
$color-grey-dark-2: rgb(197, 196, 196); // OPTION
// $color-grey-dark-2: #999;
$color-grey-dark-3: #333;

$color-white: #fff;
$color-black: #000;

//FONT

$default-font-size: 1.8rem;
$font-default: 'Montserrat', sans-serif;
$font-special: 'Alex Brush', cursive;
$font-alt: 'Inter', sans-serif;
$text-shadow: $color-grey-dark-3 1px 1px;

//STYLING

$content-margin: 2rem 0;
$content-padding: 3rem 0;
$content-padding-mobile: 1.2rem 0;
$content-border-radius: 10px;

