@use '../abstracts' as *;

.intro {
	position: relative;

	&__view {
		display: flex;
		flex-direction: column;
		justify-content: center;
		text-align: center;
		color: $color-white;
		// color: $color-primary-dark;
		background-image: linear-gradient(
				to bottom,
				rgba(0, 0, 0, 0.3),
				rgba(0, 0, 0, 0.3)
			),
			url('../assets/hero-desktop-sml-2.jpg');
		background-position: center;
		background-size: cover;
		overflow: hidden;
		box-shadow: 0 12px 12px hsla(0deg, 0%, 0%, 0.35);
		height: 100vh;

		@include respond(big-desktop) {
			background-image: linear-gradient(
					to bottom,
					rgba(0, 0, 0, 0.3),
					rgba(0, 0, 0, 0.3)
				),
				url('../assets/hero-desktop-lrg.jpg');
			background-position: center;
			background-size: cover;
		}

		@include respond(tab-port) {
			background-image: linear-gradient(
					to bottom,
					rgba(0, 0, 0, 0.3),
					rgba(0, 0, 0, 0.3)
				),
				url('../assets/hero-mobile-sml.jpg');
			background-position: center;
			background-size: cover;
		}
	}

	&__scroll {
		position: absolute;
		bottom: 5rem;
		right: 3rem;
		opacity: 0;
		transition: all ease-out 1s;

		&--label {
			width: 5rem;
			height: 5rem;
			margin-left: 1rem;
			fill: $color-white;
		}
	}

	&__scroll svg {
		transition: all ease-out 1s;
		fill: $color-white;
		// fill: $color-primary-dark;
	}

	&__stmnt {
		font-size: 5rem;
		font-weight: 500;
		padding: 0 5rem;
		word-wrap: break-word;
		transform: translate(0, 23rem);

		@include respond(tab-land) {
			font-size: 4rem;
		}

		@include respond(tab-port) {
			font-size: 3.5rem;
		}

		@include respond(phone) {
			font-size: 3rem;
		}
	}

	&__question {
		font-size: 5rem;
		font-weight: 500;
		padding: 0 5rem;
		word-wrap: break-word;
		transform: translate(0, 23rem);

		@include respond(tab-land) {
			font-size: 4rem;
		}

		@include respond(tab-port) {
			font-size: 3.5rem;
		}

		@include respond(phone) {
			font-size: 3rem;
		}
	}
}

.shift {
	display: none;
}

