@use '../abstracts' as *;

.content {
	position: relative;
	display: flex;
	flex-direction: column;
	padding: 5rem 15rem 10rem 15rem;
	font-size: 1.6rem;

	@include respond(tab-land) {
		padding: 5rem 12rem 8rem 12rem;
	}

	@include respond(tab-port) {
		padding: 4rem 10rem 6rem 10rem;
	}

	@include respond(phone) {
		padding: 4rem 2rem;
	}

	&__heading {
		font-family: $font-default;
		font-size: 6rem;
		font-weight: 700;
		color: $color-primary-dark;
		text-shadow: $text-shadow;

		@include respond(tab-land) {
			font-size: 6rem;
		}

		@include respond(phone) {
			font-size: 4rem;
		}
	}

	&__health {
		display: flex;
		flex-direction: row;
		background-color: $color-grey-light-1;
		margin: $content-margin;
		padding: $content-padding;
		border-radius: $content-border-radius;
		scroll-margin-top: 20vh;

		@include respond(phone) {
			padding: $content-padding-mobile;
			// scroll-margin-top: 35vh;
		}
	}

	&__healthy {
		color: $color-primary-dark;
		text-shadow: $text-shadow;
	}

	&__coaching {
		background-color: $color-grey-light-1;
		margin: $content-margin;
		padding: $content-padding;
		border-radius: $content-border-radius;
		scroll-margin-top: 20vh;

		&--list {
			margin-top: 3rem;
			padding-left: 3rem;
		}

		&--list li {
			padding: 0.5rem 0;
		}

		@include respond(phone) {
			padding: $content-padding-mobile;
			// scroll-margin-top: 35vh;
		}
	}

	&__about {
		background-color: $color-grey-light-1;
		margin: $content-margin;
		padding: $content-padding;
		border-radius: $content-border-radius;
		scroll-margin-top: 20vh;

		@include respond(phone) {
			padding: $content-padding-mobile;
			// scroll-margin-top: 35vh;
		}
	}

	&__faq {
		// background-color: $color-background;
		background-color: $color-grey-light-1;
		margin: $content-margin;
		padding: $content-padding;
		border-radius: $content-border-radius;
		scroll-margin-top: 20vh;

		@include respond(phone) {
			padding: $content-padding-mobile;
			// scroll-margin-top: 35vh;
		}
	}

	&__contact {
		background-color: $color-grey-light-1;
		margin: $content-margin;
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;
		padding: $content-padding;
		border-radius: $content-border-radius;
		scroll-margin-top: 20vh;

		&--heading {
			width: 100%;
			padding-bottom: 2rem;
		}

		@include respond(phone) {
			padding: $content-padding-mobile;
			// scroll-margin-top: 35vh;
		}
	}
}

button {
	font-size: 1.5rem;
	background-color: $color-primary-dark;
	border: 1px solid $color-grey-dark-2;
	border-radius: 5px;
	padding: 0.5rem 2rem;
	cursor: pointer;
}

button:hover,
button:focus {
	background-color: $color-primary-dark;
	border: 1px solid $color-grey-dark-3;
}

.content p {
	padding: 1rem 0;
}

.text-block {
	background-color: $color-grey-light-1;
	border-radius: 5px;
	padding: 2rem 20rem;

	@include respond(tab-land) {
		padding: 2rem 15rem;
	}

	@include respond(tab-port) {
		padding: 2rem 10rem;
	}

	@include respond(phone) {
		padding: 2rem 4rem;
	}
}

.appear {
	animation: appear 3s ease-out 1s;
	animation-fill-mode: forwards;
}

.quote {
	padding: 1rem;
	margin: 1rem 2rem;
	background-color: $color-grey-dark-2;
	border-radius: 5px;
	// width: max-content;
}

.nbhwc {
	font-weight: 500;
	color: rgb(19, 19, 208);
}

.nbhwc:hover,
.nbhwc:focus {
	text-decoration: underline;
}

h3 {
	padding-left: 4rem;
}

h4 {
	margin-top: 2rem;
	padding-left: 2rem;
	background-color: $color-grey-dark-2;
	border-radius: 3px;
}

