@use '../abstracts' as *;

.form {
	&__group {
		width: 40vw;
		margin: auto;

		@include respond(phone) {
			width: 70vw;
		}
	}

	&__group:not(:last-child) {
		margin-bottom: 2rem;
	}

	&__input {
		display: block;
		font-family: inherit;
		font-size: 1.5rem;
		color: inherit;
		width: 100%;
		background-color: $color-grey-light-1;
		border: 1px solid $color-grey-dark-2;
		border-bottom: 2px solid $color-grey-dark-2;
		border-radius: 3px;
		padding: 1.5rem 2rem;
		transition: all 0.3s;

		&:focus {
			outline: none;
			box-shadow: 0 1rem 2rem rgba($color-black, 0.1);
			border-bottom: 2px solid $color-primary;
		}

		&:focus:invalid {
			border-bottom: 2px solid $color-secondary-dark;
		}

		&::-webkit-input-placeholder {
			color: $color-grey-dark-2;
		}
	}

	&__label {
		display: block;
		font-size: 1.4rem;
		font-weight: 700;
		color: $color-primary-dark;
		margin-left: 2rem;
		margin-top: 0.7rem;
		transition: all 0.3s;
	}

	&__input:placeholder-shown + &__label {
		opacity: 0;
		visibility: hidden;
		transform: translateY(-4rem);
	}

	&__input label {
		display: inline;
	}
}

.btn {
	@include respond(phone) {
		width: 100%;
	}
}

.recaptcha {
	// transform: scale(0.77);
	margin: 0 auto;
}

textarea {
	resize: none;
}

