@use '../abstracts' as *;

footer {
	position: sticky;
	bottom: 0;
	left: 0;
	background-color: $color-background;
	padding: 0 15rem;

	@include respond(phone) {
		padding: 0 2rem;
	}
}

.footer__logo {
	width: 100px;
	height: 100px;

	@include respond(phone) {
		width: 60px;
		height: 60px;
	}
