	&__content {
		display: flex;
		justify-content: space-between;
		align-items: center;
		font-size: 1.25rem;
		color: rgb(89, 88, 88);
		background-color: $color-primary-darker;
		border-top-left-radius: 10px;
		border-top-right-radius: 10px;
		height: auto;
		padding: 1.75rem 3rem;
	}
	&__details {
		display: inline;
		width: 100%;
		font-size: 1.5rem;
		text-align: center;

		@include respond(phone) {
			display: flex;
			flex-direction: column;
		}
	}

	&__item {
		padding: 0 1rem;
	}

	a:link,
	a:visited {
		color: $color-black;
	}
}

